Source code in <code>dataframe_api_compat/pandas_standard/__init__.py</code> <pre><code>def is_dtype(self, dtype: DType, kind: str | tuple[str, ...]) -&gt; bool:
    if isinstance(kind, str):
        kind = (kind,)
    dtypes: set[Any] = set()
    for _kind in kind:
        if _kind == "bool":
            dtypes.add(Namespace.Bool)
        if _kind == "signed integer" or _kind == "integral" or _kind == "numeric":
            dtypes |= {
                Namespace.Int64,
                Namespace.Int32,
                Namespace.Int16,
                Namespace.Int8,
            }
        if _kind == "unsigned integer" or _kind == "integral" or _kind == "numeric":
            dtypes |= {
                Namespace.UInt64,
                Namespace.UInt32,
                Namespace.UInt16,
                Namespace.UInt8,
            }
        if _kind == "floating" or _kind == "numeric":
            dtypes |= {Namespace.Float64, Namespace.Float32}
        if _kind == "string":
            dtypes.add(Namespace.String)
    return isinstance(dtype, tuple(dtypes))
